'use strict'

// TODO add in the stop callback or something.

function Events(events){
	this.client = events
}

module.exports = function(events){
	return new Events(events);
}

Events.prototype.listen = function(filter, callback){
	var blockRange = {
		Start: {
			Type: 3,
			Index: 0
		},
		End: {
			Type: 4,
			Index: 0
		}
	}

	this.client.GetEvents(blockRange, filter, function(err, data){
		if (err) {
			return callback(err)
		}

		for (var i = 0; i < data.events.length; i++) {
			callback(null, data.events[i])
		};
	})	
}

Events.prototype.subContractEvents = function(address, options, callback){
	var filter = "EventType = 'LogEvent' AND Address = \'" + address.toUpperCase() + "\'"

	return this.listen(filter, callback)
}

